    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_peten)

        var textview: TextView = findViewById(R.id.textView29)
        var textview2: TextView = findViewById(R.id.textView31)
        var textview3: TextView = findViewById(R.id.textView33)
        var textview4: TextView = findViewById(R.id.textView35)
        var textview5: TextView = findViewById(R.id.textView37)
        var textview6: TextView = findViewById(R.id.textView39)
